    private fun initRecyclerView() {
        //Log.d(FragmentActivity.TAG, "initRecyclerView: init recyclerview")
        var recyclerViewResumen = findViewById<RecyclerView>(R.id.recyclerViewResumen)
        var botonAgregarCantidadAlimento = findViewById<Button>(R.id.button14)
        var spinnerCantidadAlimento = findViewById<Spinner>(R.id.cantidadAlimentoSpinner)
        var adapterCantidadAlimentos: ArrayAdapter<CharSequence> = ArrayAdapter.createFromResource(this,R.array.combo_cantidad_alimento, android.R.layout.simple_spinner_item)
        spinnerCantidadAlimento.adapter = adapterCantidadAlimentos

        //ALMIDON
        val layoutManagerAlmidon =
                LinearLayoutManager(this@ConteoCarbohidratos, LinearLayoutManager.HORIZONTAL, false)
        val recyclerViewAlmidon = findViewById<RecyclerView>(R.id.recyclerViewAlmidon)
        recyclerViewAlmidon.layoutManager = layoutManagerAlmidon
        val adapterAlmidon = RecyclerViewAdapter(this@ConteoCarbohidratos, mNamesAlmidon, mImageUrlsAlmidon, null, recyclerViewResumen, botonAgregarCantidadAlimento, spinnerCantidadAlimento)
        recyclerViewAlmidon.adapter = adapterAlmidon
        recyclerViewAlmidon.visibility = View.INVISIBLE

        arregloRecyclerView.add(recyclerViewAlmidon)

        //FRUTAS
        val layoutManagerFrutas =
                LinearLayoutManager(this@ConteoCarbohidratos, LinearLayoutManager.HORIZONTAL, false)
        val recyclerViewFrutas = findViewById<RecyclerView>(R.id.recyclerViewFrutas)
        recyclerViewFrutas.layoutManager = layoutManagerFrutas
        val adapterFrutas = RecyclerViewAdapter(this@ConteoCarbohidratos, mNamesFrutas, mImageUrlsFrutas, null, recyclerViewResumen, botonAgregarCantidadAlimento, spinnerCantidadAlimento)
        recyclerViewFrutas.adapter = adapterFrutas
        recyclerViewFrutas.visibility = View.INVISIBLE

        arregloRecyclerView.add(recyclerViewFrutas)

        //GRANOS
        val layoutManagerGranos =
                LinearLayoutManager(this@ConteoCarbohidratos, LinearLayoutManager.HORIZONTAL, false)
        val recyclerViewGranos = findViewById<RecyclerView>(R.id.recyclerViewGranos)
        recyclerViewGranos.layoutManager = layoutManagerGranos
        val adapterJugos = RecyclerViewAdapter(this@ConteoCarbohidratos, mNamesJugos, mImageUrlsJugos, null, recyclerViewResumen, botonAgregarCantidadAlimento, spinnerCantidadAlimento)
        recyclerViewGranos.adapter = adapterJugos
        recyclerViewGranos.visibility = View.INVISIBLE

        arregloRecyclerView.add(recyclerViewGranos)

        //LACTEOS
        val layoutManagerLacteos =
                LinearLayoutManager(this@ConteoCarbohidratos, LinearLayoutManager.HORIZONTAL, false)
        val recyclerViewLacteos = findViewById<RecyclerView>(R.id.recyclerViewLacteos)
        recyclerViewLacteos.layoutManager = layoutManagerLacteos
        val adapterLacteos = RecyclerViewAdapter(this@ConteoCarbohidratos, mNamesLacteos, mImageUrlsLacteos, null, recyclerViewResumen, botonAgregarCantidadAlimento, spinnerCantidadAlimento)
        recyclerViewLacteos.adapter = adapterLacteos
        recyclerViewLacteos.visibility = View.INVISIBLE

        arregloRecyclerView.add(recyclerViewLacteos)

        //VEGETALES
        val layoutManagerVegetales =
                LinearLayoutManager(this@ConteoCarbohidratos, LinearLayoutManager.HORIZONTAL, false)
        val recyclerViewVegetales = findViewById<RecyclerView>(R.id.recyclerViewVegetales)
        recyclerViewVegetales.layoutManager = layoutManagerVegetales
        val adapterVegetales = RecyclerViewAdapter(this@ConteoCarbohidratos, mNamesVegetales, mImageUrlsVegetales, null, recyclerViewResumen, botonAgregarCantidadAlimento, spinnerCantidadAlimento)
        recyclerViewVegetales.adapter = adapterVegetales
        recyclerViewVegetales.visibility = View.INVISIBLE

        arregloRecyclerView.add(recyclerViewVegetales)


        //PRIMER RECYCLER
        val layoutManager =
                LinearLayoutManager(this, LinearLayoutManager.HORIZONTAL, false)
        val recyclerView = findViewById<RecyclerView>(R.id.recyclerViewCategoria)
        recyclerView.layoutManager = layoutManager
        println("ESTE ES EL ID DEL RESUMEN" + recyclerViewResumen.id)
        val adapter = RecyclerViewAdapter(this, mNames, mImageUrls, arregloRecyclerView, recyclerViewResumen, botonAgregarCantidadAlimento, spinnerCantidadAlimento)
        recyclerView.adapter = adapter

    }





